mapper.select:
select story_id, user_id, story_image, created_at
from stories
where story_id = #{storyId}

select story_id, user_id, story_image, created_at, expires_at, user_name, user_avatar
from active_stories_view
where story_id = #{storyId}

mapper.update:
UPDATE stories
SET story_image = #{storyImage}
WHERE story_id = #{storyId}
